package bd.edu.seu.Backend.Repository;import bd.edu.seu.Backend.Model.Login_info;import org.springframework.data.jpa.repository.Query;import org.springframework.data.repository.CrudRepository;public interface LoginInfoRepository extends CrudRepository<Login_info,String> {    @Query(value = "SELECT * FROM login_info WHERE phone=? AND pass=?",nativeQuery = true)    public Login_info getLoginInfo(String phone,String pass);}