package bd.edu.seu.Backend.Service;import bd.edu.seu.Backend.Model.Login_info;import bd.edu.seu.Backend.Model.Role;import bd.edu.seu.Backend.Repository.LoginInfoRepository;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;@Servicepublic class LoginInfoService {    @Autowired    private LoginInfoRepository loginInfoRepository;    public void SaveLoginInfo(Login_info login_info){        loginInfoRepository.save(login_info);    }    public Login_info getLoginInfo(String phone, String pass){        try {            return loginInfoRepository.getLoginInfo(phone, pass);        }catch (Exception e){            return new Login_info();        }    }}