package bd.edu.seu.Backend.Service;import bd.edu.seu.Backend.Model.Login_info;import com.vaadin.flow.component.notification.Notification;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;@Servicepublic class LoginService {    @Autowired    private LoginInfoService loginInfoService;    public String getToken(String phone, String pass){        try {            Login_info loginInfo = loginInfoService.getLoginInfo(phone, pass);            if (!loginInfo.getPhone().isEmpty()) {                return loginInfo.getRole().toString();            }        }catch (Exception e){            Notification.show("Not found");        }        return "unknown";    }}