package bd.edu.seu.frontend;import com.vaadin.flow.component.orderedlayout.HorizontalLayout;import com.vaadin.flow.router.Route;import javax.servlet.http.HttpSession;@Route(value = "")public class MainView extends HorizontalLayout {    public MainView(HttpSession httpSession) {        super();        MemberHeader memberHeader = new MemberHeader(httpSession);        memberHeader.addAttachListener(attachEvent -> {            String user = (String) httpSession.getAttribute("user");            if(user==null) user="unknown";            if(user.equals("Admin") || user.equals("Manager") || user.equals("Member")){                String finalUser = user.toLowerCase();                memberHeader.getUI().ifPresent(ui -> ui.navigate(finalUser));            }else{                httpSession.removeAttribute("user");                memberHeader.getUI().ifPresent(ui -> ui.navigate("login"));            }        });        add(memberHeader);    }}