package bd.edu.seu.frontend;import com.vaadin.flow.component.html.Image;import com.vaadin.flow.component.menubar.MenuBar;import com.vaadin.flow.component.orderedlayout.HorizontalLayout;import javax.servlet.http.HttpSession;import java.util.stream.Stream;public class MemberHeader extends HorizontalLayout {    public MemberHeader(HttpSession httpSession) {        Image image = new Image();        image.setWidth("120px");        image.setHeight("40px");        image.setSrc("https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcRHKGqxgKzsKPAX2JUI3w6Cq5u8qNA5CSFYIQGMhs_naHXZXC3w&usqp=CAU");        MenuBar menuBar = new MenuBar();        if(httpSession.getAttribute("user")!=null && (httpSession.getAttribute("user").equals("Manager") || httpSession.getAttribute("user").equals("Admin"))){            menuBar.addItem("Back to Manager View");        }        Stream.of("Dashboard","Accounts","Meal","Activities","Evaluation","SignOut")                .forEach(menuBar::addItem);        add(image,menuBar);        menuBar.getItems().forEach(menuItem -> {            menuItem.addClickListener(menuItemClickEvent -> {                if(menuItem.getText().equals("Dashboard")){                    httpSession.setAttribute("user",httpSession.getAttribute("user"));                    menuItem.getUI().ifPresent(ui -> ui.navigate("member"));                }else if(menuItem.getText().equals("Accounts")){                    httpSession.setAttribute("user",httpSession.getAttribute("user"));                    menuItem.getUI().ifPresent(ui -> ui.navigate("memberAccounts"));                }else if(menuItem.getText().equals("Meal")) {                    httpSession.setAttribute("user", httpSession.getAttribute("user"));                    menuItem.getUI().ifPresent(ui -> ui.navigate("memberMeal"));                }else if(menuItem.getText().equals("Activities")) {                    httpSession.setAttribute("user", httpSession.getAttribute("user"));                    menuItem.getUI().ifPresent(ui -> ui.navigate("memberActivites"));                }else if(menuItem.getText().equals("Evaluation")) {                    httpSession.setAttribute("user", httpSession.getAttribute("user"));                    menuItem.getUI().ifPresent(ui -> ui.navigate("evaluation"));                }else if(menuItem.getText().equals("Back to Manager View")) {                    httpSession.setAttribute("user", httpSession.getAttribute("user"));                    menuItem.getUI().ifPresent(ui -> ui.navigate("manager"));                }else{                    httpSession.removeAttribute("user");                    menuItem.getUI().ifPresent(ui -> ui.navigate("login"));                }            });        });    }}